INSERT INTO `janus__entity` VALUES (430,'google.com/a/student.s-res.uva.nl',11,'testaccepted','saml20-sp',NULL,NULL,NULL,NULL,'no',177,'// Look for uva - google - id within all entitlements\r\n\r\n// When entitlement not found default to \'NotAllowed\'\r\n$subjectId = \'NotAllowed\';\r\n\r\nif (isset($attributes) and ($attributes !== FALSE)) {\r\n    $uvaGoogleEntitlementStart = \'urn:surf.nl:uva.nl:entitlement:google:id:\';\r\n\r\n    // Parse only the entitlements\r\n    if (!empty($attributes[\'urn:mace:dir:attribute-def:eduPersonEntitlement\'])) {\r\n        //loop over entitlements\r\n        foreach ($attributes[\'urn:mace:dir:attribute-def:eduPersonEntitlement\'] as $entitlement) {\r\n            // if entitlement starts with urn:surf.nl:uva.nl:entitlement:google:id:\r\n            if (strpos($entitlement, $uvaGoogleEntitlementStart) === 0) {\r\n                $subjectId = substr($entitlement, strlen($uvaGoogleEntitlementStart));\r\n                break;\r\n            }\r\n        }\r\n    }\r\n    // Remove all attributes\r\n    $attributes = array();\r\n}',47,'2013-03-18T14:27:51+01:00','145.97.21.54',10,'Set ARP to entitlements and adapt Attribute Manipulations to loop over all entitlements (CXT-885)','yes');
